    public static List<Question> getBathByIds(Integer quantity){
        List<Question> questions = new ArrayList<>();
        for(int i=0;i<quantity;i++){
            questions.add(questionList.get(i));
        }
        return questions;
    }
    public static List<Integer> getRandomQuestion(Integer quantity){
        List<Question> questions = new ArrayList<>();
        for (int i=0;i<quantity;i++){
            int randomInt = RandomUtil.randomInt(0, questionList.size());
            questions.add(questionList.get(randomInt));
        }
        List<Integer> questionIds = new ArrayList<>();
        for (Question question : questions) {
            questionIds.add(question.getId());
        }
        if(questionIds.size()!=quantity){
            return null;
        }
        return questionIds;
    }
